changeSet.createProcedure:
create or replace function update_audit_columns()
returns trigger as $$
begin
    if
        tg_op = 'insert' then
            new.created_at := current_timestamp;
            new.updated_at := current_timestamp;
    elsif
        tg_op = 'update' then
            new.updated_at := current_timestamp;
    end if;
return new;
end;
$$
language plpgsql;
